import './App.css';
import WorkBar from './component/WorkBar';
import WorkSpace from './component/WorkSpace';
import boardData from './util/board';
import { useState } from 'react';

function App() {
  const [currentColor, setCurrentColor] = useState('white');
  const [tbdt, setTbdt] = useState(boardData());
  const [render, setRender] = useState(false);

  function rerender() {
    setRender(prev => !prev);
  }

  function setColor(i, j) {
    setTbdt(prev => {
      prev[i][j].color = currentColor;
      return prev;
    })
  }

  return (
    <div className="App">
      <WorkBar currentColor={currentColor} setCurrentColor={setCurrentColor}></WorkBar>
      <WorkSpace rerender={rerender} currentColor={currentColor} tbdt={tbdt} setColor={setColor}></WorkSpace>
      <br></br>
      <button>save</button>
      <button>init</button>
    </div>
  );
}

export default App;
