export default function WorkSpace(props) {

    const tb = props.tbdt.data.map(d1 => {
        return <tr>{
            d1.map(d2 => {
                return (
                    <td onClick={() => {
                        props.setColor(d2.i, d2.j)
                        props.rerender()
                    }} onDoubleClick={()=> props.clickForDrag()} 
                    onMouseEnter={() => { console.log(d2.i, d2.j); props.setDragColor(d2.i, d2.j); props.rerender()}}                    >   
                    </td>
                )
            })
        }</tr>
    })

    return (
        <div>
            <input placeholder="제목을입력하세요" value={props.tbdt.title}></input>
            <table ondragstart="return true">
                {tb}
            </table>
        </div>
    )
}
