import './App.css';
import WorkBar from './component/WorkBar';
import WorkSpace from './component/WorkSpace';
import boardData from './util/board';
import Sidebar from './component/SideBar';
import Split from "react-split";
import { useState, useEffect } from 'react';



function App() {
  const [currentColor, setCurrentColor] = useState('white');
  const [tbdt, setTbdt] = useState(() => JSON.parse(localStorage.getItem("works")) || boardData());
  const [render, setRender] = useState(false);

  function rerender() {
    setRender(prev => !prev);
  }

  function setColor(i, j) {
    setTbdt(prev => {
      prev[i][j].color = currentColor;
      return prev;
    })
  }

  useEffect(() => {
    localStorage.setItem("works", JSON.stringify(tbdt));
  }, [render]);

  return (
    <main>
        <Split sizes={[30, 70]} direction="horizontal" className="split">
          <Sidebar/>
          <div className='App'>
            <WorkBar currentColor={currentColor} setCurrentColor={setCurrentColor}></WorkBar>
            <WorkSpace rerender={rerender} currentColor={currentColor} tbdt={tbdt} setColor={setColor}></WorkSpace>
          </div>
        </Split>
    </main>
  );
}

export default App;
