import boardData from '../util/board'
import { useEffect, useState } from 'react'

export default function WorkSpace(props) {

    const tb = props.tbdt.map(d1 => {
        return <tr>{
            d1.map(d2 => {
                return (
                    <td onClick={() => {
                        props.rerender()
                        props.setColor(d2.i, d2.j)
                    }} style={{backgroundColor: d2.color}}>{d2.color}</td>
                )
            })
        }</tr>
    })

    return (
        <div>
            <table>
                {tb}
            </table>
        </div>
    )
}
