import boardData from '../util/board'
import { useEffect, useState } from 'react'

export default function WorkSpace(props) {

    const [tbdt, setTbdt] = useState(boardData());

    function changeColor(i, j) {
        setTbdt(prev => tbdt[i][j].color = props.currentColor)
    }

    const tb = tbdt.map(d1 => {
        return <tr>{
            d1.map(d2 => {
                return (
                    <td onClick={() => changeColor(d2.i, d2.j)} style={{backgroundColor: d2.color}}>{d2.color}</td>
                )
            })
        }</tr>
    })

    return (
        <div>
            <table clasName="">
                {tb}
            </table>
        </div>
    )
}
