import './App.css';
import WorkBar from './component/WorkBar';
import WorkSpace from './component/WorkSpace';
import boardData from './util/board';
import Sidebar from './component/SideBar';
import Split from "react-split";
import { useState, useEffect } from 'react';
import {nanoid } from 'nanoid';
import Hover from './component/Hover';

function App() {
  const [clicking, setClicking] = useState(false);
  const [currentColor, setCurrentColor] = useState('white');
  const [tbdt, setTbdt] = useState(() => JSON.parse(localStorage.getItem("works")) || []);
  const [currentWorkId, setCurrentWorkId] = useState(tbdt[0] && tbdt[0].id || "");
  const [render, setRender] = useState(false);

  function clickForDrag() {
    setClicking(prev => !prev);
  }

  function rerender() {
    setRender(prev => !prev);
  }

  function setColor(i, j) {
    if(clicking) {
      setTbdt(prev => {
        findCurrentWork().data[i][j].color = currentColor;
        return prev;
      })
    }
  }

  function findCurrentWork() {
    return (
      tbdt.find((work) => {
        return (work.id === currentWorkId)
      }) || tbdt[0]
    )
  }

  function newWork() {
    let dt = boardData();
    setTbdt(prev => [{
      id: nanoid(),
      title:"input title", 
      data : dt
    }, ...prev]);
  }

  useEffect(() => {
    localStorage.setItem("works", JSON.stringify(tbdt));
  }, [render]);

  return (
    <main>
      <Hover></Hover>
      {tbdt.length > 0 ? (<Split sizes={[15, 85]} direction="horizontal" className="split">
          <Sidebar tbdt={tbdt} currentWorkId={currentWorkId} setCurrentWorkId={setCurrentWorkId} />
          <div className='App'>
            <WorkBar currentColor={currentColor} setCurrentColor={setCurrentColor}></WorkBar>
            <WorkSpace rerender={rerender} currentColor={currentColor} tbdt={findCurrentWork()} setColor={setColor}clicking={clicking} clickForDrag={clickForDrag}></WorkSpace>
          </div>
        </Split>) : 
        <div className="App">
            <h1>No Data..</h1>
            <p>start to click below button</p>
            <button onClick={() => {
              newWork()
            }}>new work</button>
          </div>}
    </main>
  );
}

export default App;
