import boardData from '../util/board'
import { useEffect, useState } from 'react'

export default function WorkSpace(props) {
    const tbdt = props.tbdt;
    const setTbdt = props.setTbdt;

    const tb = tbdt.map(d1 => {
        return <tr>{
            d1.map(d2 => {
                return (
                    <td onClick={() => setTbdt(prev => {
                        prev[d2.i][d2.j].color = props.currentColor;
                        return prev;
                    })} style={{backgroundColor: d2.color}}>{d2.color}</td>
                )
            })
        }</tr>
    })

    return (
        <div>
            <table>
                {tb}
            </table>
        </div>
    )
}
